#function for computing the estimated probabilities for each of the adaptive intervention
prob_est_func = function(data,t1,t2){
  pattr = which(data[,1] == t1 & data[,2] == t2) #checking number of patients in intervention
  suctr = sum(as.numeric(c(data[pattr,3]))) #checking number of success in intervention
  if(length(pattr) == 0){
    p_tr = 0}else{
      p_tr = suctr/length(pattr)
    }
  if(p_tr == 0)
    p_tr = 0.01 #if there is no patient with success then taking the least value as success probability
  if(p_tr == 1)
    p_tr = 0.99 #if all patients are success then taking highest value as prob
  return(p_tr)
}

#function for assigning patients to treatments, and also generating the outcome of the treatment
#intervention for the patient

trt_outcome_gen_func = function(data,sr_no,resp_a,resp_b,alloc_tr_a,alloc_tr_ac,alloc_tr_be,suc_aa,suc_ac,suc_ad,suc_bb,suc_be,suc_bf){
  fr = rbinom(1,1,alloc_tr_a) #generating random number for allocating first stage treatment
  #i=i+1
  if(fr == 1){
    data[sr_no,1] = "A" #if 1 is generated then A is given
    #set.seed(123+i)
    sr = rbinom(1,1,resp_a) #generating random number for responder status
    #i=i+1
    if(sr == 1){
      data[sr_no,2] = "A"
      #set.seed(123+i)
      data[sr_no,3] = rbinom(1,1,suc_aa) #generating outcome for (A,A)
      #i=i+1
    }else{
      #set.seed(123+i)
      srr = rbinom(1,1,alloc_tr_ac) #generating random number for deciding treatment C or D
      #i=i+1
      if(srr == 1){
        data[sr_no,2] = "C"
        #set.seed(123+i)
        data[sr_no,3] = rbinom(1,1,suc_ac) #generating outcome for (A,C)
        #i=i+1
      }else{
        data[sr_no,2] = "D"
        #set.seed(123+i)
        data[sr_no,3] = rbinom(1,1,suc_ad) #generating outcome for (A,D)
        #i=i+1
      }
    }
  }else{
    data[sr_no,1] = "B"
    #set.seed(123+i)
    sr = rbinom(1,1,resp_b) #generating random number for treatment B responder status
    #i=i+1
    if(sr == 1){
      data[sr_no,2] = "B"
      #set.seed(123+i)
      data[sr_no,3] = rbinom(1,1,suc_bb) #generating outcome for (B,B)
      #i=i+1
    }else{
      #set.seed(123+i)
      srr = rbinom(1,1,alloc_tr_be) #generating random number for treatment E or F
      #i=i+1
      if(srr == 1){
        data[sr_no,2] = "E"
        #set.seed(123+i)
        data[sr_no,3] = rbinom(1,1,suc_be) #generating outcome for (B,E)
        #i=i+1
      }else{
        data[sr_no,2] = "F"
        #set.seed(123+i)
        data[sr_no,3] = rbinom(1,1,suc_bf) #generating outcome for (B,F)
      }
    }
  }
  return(data) #returning the new data matrix
}

resp_est_prob = function(data,t1,t2){
  pattr = which(data[,1] == t1 & data[,2] == t2) #checking number of patients in intervention
  detr = which(data[,1] == t1)
  if(length(pattr) == 0){
    p_tr = 0}else{
      p_tr = length(pattr)/length(detr)
    }
}

round.off <- function (x, digits) 
{
  posneg = sign(x)
  z = trunc(abs(x) * 10 ^ (digits + 1)) / 10
  z = floor(z * posneg + 0.5) / 10 ^ digits
  return(z)
}


